INSERT INTO bookstoredb.book (Id, Title, Description, Author, AddedDate, CategoryId, Image, Price) VALUES (1, 'Selfish Gene', 'The Selfish Gene test is a 1976 book on evolution by Richard Dawkins, in which the author builds upon the principal theory of George C. Williams''s Adaptation and Natural Selection (1966). Dawkins uses the term "selfish gene" as a way of expressing the gene-centred view of evolution as opposed to the views focused on the organism and the group, popularising ideas developed during the 1960s by W. D. Hamilton and others. From the gene-centred view, it follows that the more two individuals are genetically related, the more sense (at the level of the genes) it makes for them to behave selflessly with each other.', 'Richard Dawkins', '2018-07-28 11:12:27', 1, 'img/product-img/rd.jpg', 125);
INSERT INTO bookstoredb.book (Id, Title, Description, Author, AddedDate, CategoryId, Image, Price) VALUES (2, 'A Universe from Nothing', 'A Universe from Nothing: Why There Is Something Rather than Nothing is a non-fiction book by the physicist Lawrence M. Krauss, initially published on January 10, 2012 by Free Press. It discusses modern cosmogony and its implications for the debate about the existence of God. ', 'Lawrence Krauss', '2018-07-25 15:32:46', 1, 'img/product-img/ufn.jpg', 85);
INSERT INTO bookstoredb.book (Id, Title, Description, Author, AddedDate, CategoryId, Image, Price) VALUES (3, 'Der Process', 'The Trial (original German title: Der Process,[1] later Der Proceß, Der Prozeß and Der Prozess) is a novel written by Franz Kafka between 1914 and 1915 and published posthumously in 1925. One of his best-known works, it tells the story of Josef K., a man arrested and prosecuted by a remote, inaccessible authority, with the nature of his crime revealed neither to him nor to the reader', 'Franz Kafka', '2018-07-25 15:32:47', 2, 'img/product-img/ksder.jpg', 45);
INSERT INTO bookstoredb.book (Id, Title, Description, Author, AddedDate, CategoryId, Image, Price) VALUES (4, 'The Metamorphosis', 'The Metamorphosis (German: Die Verwandlung) is a novella written by Franz Kafka which was first published in 1915. One of Kafka''s best-known works, The Metamorphosis tells the story of salesman Gregor Samsa who wakes one morning to find himself inexplicably transformed into a huge insect and subsequently struggling to adjust to this new condition.', 'Franz Kafka', '2018-07-25 15:32:47', 2, 'img/product-img/metkaf.jpg', 40);
INSERT INTO bookstoredb.book (Id, Title, Description, Author, AddedDate, CategoryId, Image, Price) VALUES (5, 'The Art of Computer Programming', 'The Art of Computer Programming (sometimes known by its initials TAOCP) is a comprehensive monograph written by Donald Knuth that covers many kinds of programming algorithms and their analysis.', 'Donald Knuth', '2018-07-25 15:32:47', 3, 'img/product-img/kn.jpg', 128);
INSERT INTO bookstoredb.book (Id, Title, Description, Author, AddedDate, CategoryId, Image, Price) VALUES (6, 'Pro .Net Best Practices', 'Pro .NET Best Practices is a practical reference to the best practices that you can apply to your .NET projects today. You will learn standards, techniques, and conventions that are sharply focused, realistic and helpful for achieving results, steering clear of unproven, idealistic, and impractical recommendations.', 'Stephen Ritchie', '2018-07-25 15:32:47', 3, 'img/product-img/dr.jpg', 99);
